        title: '前端框架，你最爱哪一个',
        author: 'starry',
        date: '2019.09.09',
        like: 12000
      },{
        title: '前端框架，你最爱哪一个',
        author: 'starry',
        date: '2019.09.09',
        like: 12000
      }
    ]
    setTimeout(() => {
      this.setState({
        posts: data,
        newPost: false
      })
    }, 200);
  }

  render() {
    return (
      <PostsView posts={this.state.posts}/>
    )
  }
}

export default Postlist;
